async function link_recent_note(tp) {
    const allFiles = app.vault.getMarkdownFiles();

    // 最近 20 条
    const recentFiles = [...allFiles]
        .sort((a, b) => b.stat.mtime - a.stat.mtime)
        .slice(0, 20);

    // 在近期列表末尾加一个“搜索更多”选项
    const options = [...recentFiles, { basename: "🔍 搜索更多…" }];

    // 第一次选择：近期笔记或“搜索更多”
    const firstChoice = await tp.system.suggester(
        f => f.basename,
        options,
        false,
        "选择关联笔记（或搜索更多）"
    );

    let selectedFile = firstChoice;

    // 如果选择了“搜索更多”，再弹出全库选择器
    if (firstChoice && firstChoice.basename === "🔍 搜索更多…") {
        selectedFile = await tp.system.suggester(
            f => f.basename,
            allFiles.sort((a, b) => a.basename.localeCompare(b.basename)),
            false,
            "搜索全库笔记"
        );
    }

    return selectedFile ? `[[${selectedFile.basename}]]` : "无";
}

module.exports = link_recent_note;
